sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();
	var tomorrow = gs.hoursAgo(-24);//rightNow.addDaysLocalTime(1);
    //var mytomorrow = rightNow.addDaysLocalTime(1);

	// Query the database for NeedIt Task records with Due date field values older
	// than the current time.  Only return NeedIt Task records that do not have
	// a State field value of Closed Complete.
	var NextNITasks = new GlideRecord('x_58872_needit_needit_task');
	NextNITasks.addQuery('due_date','>=',rightNow);
	NextNITasks.addQuery('due_date','<',tomorrow);
	NextNITasks.addQuery('state','!=',3);

	NextNITasks.query();
	// Write a log message for each overdue NeedIt Task Record
	while(NextNITasks.next()){
		gs.info("NeedIt Task due soon record =" + NextNITasks.number);
		gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',NextNITasks,NextNITasks.number,gs.getUserName());
	}
